def shift(xs, n):
    if n &gt;= 0:
        return np.concatenate((np.full(n, xs[0]), xs[:-n]))
    else:
        return np.concatenate((xs[-n:], np.full(-n, xs[-1])))

def f_hat_num(x,t):
    delta = shift(t, -1) - t
    salida = (shift(x, -1) - x) / np.mean(delta[:-1])
    return np.concatenate((salida[:-1], [salida[-2]]))


